import React from 'react';


const ProductCard = (props) => {
    const product = props.product;

    const getAmount = (array,value) => {
        return array.filter((item) => (item.name === value)).length;
    }
    const getPosition = (myArray,name) => {
        for (let i = 0; i < myArray.length; i++) {
            if (myArray[i].name === name) {
                alert('position: ' + i);
                return i;
            }
        }
    }

    const addToCart = () => {
        var restoredCart = JSON.parse(localStorage.getItem('shoppingCart'));
        let amount = getAmount(restoredCart.cart,product.name) + 1;

        restoredCart.cart.push({
            name: product.name,
            amount: amount,
            price: product.price,
        });

        let total = restoredCart.total + product.price;
        restoredCart.total= total;

        localStorage.setItem('shoppingCart', JSON.stringify(restoredCart));
    }

    return (
        <div className="card mb-3" style={{maxWidth: 540}}>
            <div className="row no-gutters">
                <div className="col-md-4">
                    <img src="..." className="card-img" alt="..." />
                </div>
                <div className="col-md-8">
                    <div className="card-body">
                        <h5 className="card-title">{product.name}
                        <p className="card-text">${product.price}</p>
                        </h5>
                        <p className="card-text">{product.desc}</p>
                    </div>
                </div>
            </div>
            <button  class="btn btn-secondary" onClick={addToCart}>ADD TO CART</button>
        </div>
    )
}

export default ProductCard;
